import React from 'react'
import BBox from './BBox'

class ImageEditor extends React.Component {

    constructor(props) {
        super(props)

        this.bBoxes = []
        this.formData = { xPos: 0, yPos: 0, width: 0, height: 0 }
        this.message = ""
        this.sizeHint = ""

        this.imageRef = null


        this.setImageRef = element => {
            this.imageRef = element
        }

        // Dynamically syncs FormData with input values
        this.handleChange = (e) => {
            console.log('handleChange', e.target.id)
            const updatedValue = { [e.target.id]: e.target.value }
            this.formData = {
                ...this.formData, ...updatedValue
            }
        }


        // Adds a Box to the Array and Resets the Form
        this.handleSumbit = (e) => {
            e.preventDefault()

            // Check if Integers
            try {
                Object.keys(this.FormData).forEach(function (key, value) {
                    this.forEach[key] = parseInt(value)
                })
            }
            catch {
                this.message = 'Please enter valid numbers.'
            }

            const newBBox = {
                xPos: (this.formData.xPos / this.imageRef.current.clientWidth) * 100,
                yPos: (this.formData.yPos / this.imageRef.current.clientHeight) * 100,
                width: (this.formData.width / this.imageRef.current.clientWidth) * 100,
                height: (this.formData.height / this.imageRef.current.clientHeight) * 100
            }

            console.log()

            // Check if > 0 


            if (this.formData.width <= 0 || this.formData.height <= 0) {
                this.message = 'Please add a width and height'

            }

            // Check if within image bounds 
            else if (newBBox.xPos + newBBox.width > 100
                || newBBox.yPos + newBBox.height > 100) {
                this.message = 'Your box overflows the image. Please draw a box that fits into ' + this.imageRef.current.clientWidth + 'px x ' + this.imageRef.current.clientHeight + 'px.'

            }
            else {
                console.log("new Box: ", newBBox)
                this.bBoxes.push(newBBox)
                this.formData = { xPos: 0, yPos: 0, width: 0, height: 0 }
                this.message = 'Box added'
            }

        }



    }

    render() {
        return (
            <div id={this.props.index}>

                { /* ----------- IMAGE AND BOXES ----------- */}

                <div className="image-container">
                    <img ref={this.setImageRef} src={this.props.source} alt={'the uploaded image: ' + (this.props.index + 1)} />
                    {
                        this.bBoxes.map((value, index) => {
                            return (
                                <BBox key={index} props={value} />
                            )
                        })
                    }
                </div>
                <div className="editor">



                    { /* ----------- FORM AREA ----------- */}

                    <p className="hint-message">{this.message}</p>

                    <form onSubmit={this.handleSumbit}>
                        <div className="input-group">
                            <label htmlFor="xPos">
                                <span> X </span>
                                <input id="xPos" value={this.formData.xPos} onChange={this.handleChange} placeholder="0" type="number" min="0"></input>
                            </label>
                            <label htmlFor="yPos">
                                <span> Y </span>
                                <input id="yPos" value={this.formData.yPos} onChange={this.handleChange} placeholder="0" type="number" min="0"></input>
                            </label>
                        </div>
                        <div className="input-group">
                            <label htmlFor="width">
                                <span> Width </span>
                                <input id="width" value={this.formData.width} onChange={this.handleChange} placeholder="0" type="number" min="0"></input>
                            </label>
                            <label htmlFor="height">
                                <span> Height </span>
                                <input id="height" value={this.formData.height} onChange={this.handleChange} placeholder="0" type="number" min="0"></input>
                            </label>
                        </div>
                        <input type="submit" />
                    </form>
                    <div>
                    </div>
                </div>
            </div>
        )
    }
}


export default ImageEditor
