import React from 'react'
import BBox from './BBox'

class ImageEditor extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
            formData: { xPos: 0, yPos: 0, width: 0, height: 0 },
            bBoxes: [],
            message: '',
            sizeHint: '',
        }


        // Set Image
        this.imageRef = null
        this.setImageRef = element => {
            this.imageRef = element
            console.log(this.imageRef.hei)

        }

        // Dynamically syncs FormData with input values
        this.handleChange = (e) => {
            const value = parseInt(e.target.value)
            this.setState({formData: { ...this.state.formData, [e.target.id]: value } })
            console.log(this.state)
        }


        // Adds a Box to the Array and Resets the Form
        this.handleSumbit = (e) => {
            e.preventDefault()

            const width = this.state.formData.width
            const height = this.state.formData.height
            const xPos = this.state.formData.xPos
            const yPos = this.state.formData.yPos

            const image = this.imageRef

            // Check if > 0 
            if (width <= 0 || height <= 0) {
                const message = 'Please add a width and height'
                console.log(message)
                this.setState({message: message })
                console.log(this.state)
                return
            }

            // Check if within image bounds 
            if (yPos + width > image.clientWidth
                || yPos + height > image.clientHeight) {
                const message = 'Your box overflows the image. Please draw a box that fits into ' + image.clientWidth + 'px x ' + image.clientHeight + 'px.'
                this.setState({message: message })
                console.log(this.state)

                console.log('Your box overflows the image. Please draw a box that fits into ' + image.clientWidth + 'px x ' + image.clientHeight + 'px.')
                return
            }

            // Create new box with relative positioning for responsiveness
            const newBBox = {
                xPos: (xPos / image.clientWidth) * 100,
                yPos: (yPos / image.clientHeight) * 100,
                width: (width / image.clientWidth) * 100,
                height: (height / image.clientHeight) * 100
            }

            console.log(newBBox)

            // Draw new Box and reset form
            const message = 'Box added'
            this.setState({
                bBoxes: [...this.state.bBoxes, newBBox],
                formData: { xPos: 0, yPos: 0, width: 0, height: 0 },
                message: message
            })

        }


    }

    render() {
        return (
            <div id={this.props.index}>

                { /* ----------- IMAGE AND BOXES ----------- */}

                <div className="image-container">
                    <img ref={this.setImageRef} src={this.props.source} alt={'the uploaded image: ' + (this.props.index + 1)} />
                    {
                        this.state.bBoxes.map((value, index) => {
                            return (
                                <BBox key={index} props={value} />
                            )
                        })
                    }
                </div>
                <div className="editor">



                    { /* ----------- FORM AREA ----------- */}

                    <p className="hint-message">{this.state.message}</p>

                    <form onSubmit={this.handleSumbit}>
                        <div className="input-group">
                            <label htmlFor="xPos">
                                <span> X </span>
                                <input id="xPos" value={this.state.formData.xPos} onChange={this.handleChange} placeholder="0" type="number" min="0"></input>
                            </label>
                            <label htmlFor="yPos">
                                <span> Y </span>
                                <input id="yPos" value={this.state.formData.yPos} onChange={this.handleChange} placeholder="0" type="number" min="0"></input>
                            </label>
                        </div>
                        <div className="input-group">
                            <label htmlFor="width">
                                <span> Width </span>
                                <input id="width" value={this.state.formData.width} onChange={this.handleChange} placeholder="0" type="number" min="0"></input>
                            </label>
                            <label htmlFor="height">
                                <span> Height </span>
                                <input id="height" value={this.state.formData.height} onChange={this.handleChange} placeholder="0" type="number" min="0"></input>
                            </label>
                        </div>
                        <input type="submit" />
                    </form>
                    <div>
                    </div>
                </div>
            </div>
        )
    }
}


export default ImageEditor
