import React, { useState } from 'react'
import './style/index.css';
import UploadIcon from './img/upload.svg'
import ImageEditor from './components/ImageEditor';

const App = () => {

  const [imageSources, setImageSources] = useState([])

  return (
    <div className="App">

      <p><label HtmlFor="file" style="cursor: pointer;"> <img src={UploadIcon} alt="upload icon" />Add new Image</label></p>
      <input type="file"  accept="image/jpeg, image/png" name="image" id="file" style="display: none;"/>


      {
        imageSources.map((source, index) => {
          return (
            <ImageEditor key={index} source={source} />
          )
        })
      }

    </div>
  );
}

export default App;
