import React, { useState } from 'react'
import './style/index.css';
import UploadIcon from './img/upload.svg'
import ImageEditor from './components/ImageEditor';

class App extends React.Component {

  constructor (props) {
    super(props)
    this.state = {imageSources: []}

    this.handleUpload = this.handleUpload.bind(this)
    this.deleteImage = this.deleteImage.bind(this)
  }


  handleUpload = (e) => {
    console.log('handleUpload')
    var src = ''
    try {
      src = URL.createObjectURL(e.target.files[0])
    }
    catch {
      console.log('cancelled')
      return 
    }
    this.setState({imageSources: [...this.state.imageSources, src]})
  }


  deleteImage = (image) => {
    console.log('before: ', this.state.imageSources)
    console.log(image)
    const index = this.state.imageSources.indexOf(image)
    console.log(index)
    if(index > -1){
      this.setState({ imageSources: this.state.imageSources.splice(index, 1)})
    }
    console.log('after: ', this.state.imageSources)
  }


  render () {
    return (
    <div className="App">


    {/*---- LIST OF IMAGE EDITORS ---- */}
      {this.state.imageSources.map((item, index) => {
        return (
          <div key={index}>
            <ImageEditor source={item} index={index}/>
            <button onClick={(item) => this.deleteImage}> delete </button>
          </div>
        )
      })}

      {/*---- ADD BUTTON ---- */}
      <form>
        <label id="upload-image-button" htmlFor="file"> Add new Image
          <input onChange={this.handleUpload} type="file" accept="image/jpeg, image/png" name="image" id="file" /></label>
      </form>
    </div>
  );
}
}

export default App;
