import React, { useState } from 'react'
import './style/index.css';
import UploadIcon from './img/upload.svg'
import ImageEditor from './components/ImageEditor';
import EditorList from './components/EditorList'

const App = () => {

  const [imageSources, setImageSources] = useState([])

  const handleUpload = (e) => {
    console.log('handleUpload')
    var src = URL.createObjectURL(e.target.files[0])
    imageSources.push(src)
    setImageSources([...imageSources])
    console.log(imageSources)
  }

  return (
    <div className="App">

      <form>
        <label id="upload-image-button" htmlFor="file"> Add new Image
          <input onChange={handleUpload} type="file" accept="image/jpeg, image/png" name="image" id="file" /></label>
      </form>
      {imageSources.length}


      <EditorList props={imageSources}/>
    </div>
  );
}

export default App;
