import React, {useState, useRef, useLayoutEffect} from 'react'
import BBox from './BBox'

const ImageEditor = ({source, index})  => {

    const [bBoxes, setBBoxes] = useState([])
    const [formData, setFormData] = useState({xPos: 0, yPos: 0, width: 0, height: 0})
    const [maxHeight, setMaxHeight] = useState(0)
    const [maxWidth, setMaxWidth] = useState(0)

    const imageRef = useRef(null)

    useLayoutEffect(() => {
        setMaxHeight(imageRef.current.clientHeight)
        setMaxWidth(imageRef.current.clientWidth)
    }, [])


    // Adds a Box to the Array and Resets the Form
    const handleSumbit = (e) => {
        e.preventDefault()
        const newBBox = {...formData}
        bBoxes.push(newBBox)
        setBBoxes(bBoxes)
        setFormData({xPos: 0, yPos: 0, width: 0, height: 0 })
    }

    // Dynamically syncs FormData with input values
    const handleChange = (e) => {
        const updatedValue = {[e.target.id]: e.target.value}
        setFormData(formData => ({
            ...formData, ...updatedValue
        }))
        console.log(imageRef)
    }

    return (
       <div>

            { /* ----------- IMAGE AND BOXES ----------- */ }

            <div className="image-container">
                <img ref={imageRef} src={source} alt={'the uploaded image: ' + (index+1)}/>
                {imageRef.current?.height}
                {
                    bBoxes.map((value, i) => {
                        return (
                            <BBox key={i} props={value}/>
                        )
                    })
                }

            </div>
            <div className="editor">



                { /* ----------- FORM AREA ----------- */ }

                <form onSubmit={handleSumbit}>
                    <div className="input-group">
                        <label htmlFor="xPos">
                            <span> X </span>
                            <input id="xPos" value={formData.xPos} onChange={handleChange} placeholder="0" type="number" min="0" max={maxHeight}></input>
                        </label>
                        <label htmlFor="yPos">
                            <span> Y </span>
                            <input id="yPos" value={formData.yPos} onChange={handleChange} placeholder="0"  type="number" min="0" max={maxHeight}></input>
                        </label>
                    </div>
                    <div className="input-group">
                        <label htmlFor="width">
                            <span> Width </span>
                            <input id="width" value={formData.width} onChange={handleChange} placeholder="0"  type="number" min="0" max={100}></input>
                        </label>
                        <label htmlFor="height">
                            <span> Height </span>
                            <input id="height" value={formData.height} onChange={handleChange} placeholder="0" type="number" min="0" max={1000}></input>
                        </label>
                    </div>
                    <input type="submit" />
                </form>
                <div>
                </div>
            </div>
        </div>
    )
}

export default ImageEditor
