import React from 'react'
import BBox from './BBox'

class ImageEditor extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
            formData: { xPos: '', yPos: '', width: '', height: '' },
            bBoxes: [],
            message: '',
            focus: false,
            image: {}
        }

        this.targetVW = .8
        this.targetVH = .57

        this.imageRef = null

        this.setImageRef = this.setImageRef.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.handleSumbit = this.handleSumbit.bind(this)
        this.setImageSize = this.setImageSize.bind(this)
    }


        // Set Image
        setImageRef = () => {
            this.setImageRef = element => {
            this.imageRef = element
        }
    }


        // Syncs FormData with input values
        handleChange = (e) => {
            if (e.target.value !== '') {
                this.setState({ formData: { ...this.state.formData, [e.target.id]: parseInt(e.target.value) } })
            }
            else {
                this.setState({ formData: { ...this.state.formData, [e.target.id]: '' } })
            }

        }

        // Adds a Box to the Array and Resets the Form
        handleSumbit = (e) => {
            e.preventDefault()

            const width = this.state.formData.width
            const height = this.state.formData.height
            const xPos = this.state.formData.xPos
            const yPos = this.state.formData.yPos

            const image = this.props.image

            // Check if > 0 
            if (width <= 0 || height <= 0) {
                const message = 'Please add a width and height'
                this.setState({ message: message })
                return
            }

            // Check if within image bounds 
            if (xPos + width > image.width
                || yPos + height > image.height) {
                const message = 'Your box overflows the image. Please draw a box that fits into ' + image.clientWidth + 'px x ' + image.clientHeight + 'px.'
                this.setState({ message: message })
                return
            }

            // Create new box with relative positioning for responsiveness
            const newBBox = {
                xPos: (xPos / this.props.image.width) * 100,
                yPos: (yPos / this.props.image.height) * 100,
                width: (width / this.props.image.width) * 100,
                height: (height / this.props.image.height) * 100
            }

            // Draw new Box and reset form
            this.setState({
                bBoxes: [...this.state.bBoxes, newBBox],
                formData: { xPos: '', yPos: '', width: '', height: '' },
                message: '',
                focus: true
            })

        }

        // This Function calculates the scale factor by comparing the
        // original image dimentions with the targeted size of the element

        setImageSize = () => {

            //To do fix this
            const imageOrientation = this.props.image.width > this.props.image.height ? 'landscape' : 'portrait'
            console.log("width: ", this.props.image.width)

            console.log(imageOrientation)
            if (imageOrientation === "landscape") {
                const vw = 100*this.targetVW
                return { maxWidth: `${vw}%` }
            }
            else {
                const vh = 100 * this.targetVH
                return { maxHeight: `${vh}vh` }
            }
        }


// This fixes a weird rendering race condition where the image dimensions are loaded a second after mounting and not updated
 componentDidMount() {
     setTimeout( () => {this.setState({
        image: this.props.image
     })}, 100)
     setTimeout(this.setImageSize, 100)
     console.log(this.state)
 }


    render() {
        return (
            <div class="editor">

                { /* ----------- IMAGE AND BOXES ----------- */}

                <div className="image-container">
                    <img style={this.setImageSize()} className={this.state.focus ? '--focus' : ''} ref={this.setImageRef} src={this.props.image.src} alt={'the uploaded image: ' + (this.props.index + 1)} />
                    {
                        this.state.bBoxes.map((value, index) => {
                            return (
                                <BBox key={index} props={value} />
                            )
                        })
                    }
                </div>

                <p>Image dimension: {this.props.image.width} x {this.props.image.height}</p>

                <div>



                    { /* ----------- FORM AREA ----------- */}

                    <p className="hint-message">{this.state.message}</p>
                    <form class="b-box-form" onSubmit={this.handleSumbit}>
                        <div className="input-group">
                            <label htmlFor="xPos">
                                <span> X </span>
                                <input id="xPos" value={this.state.formData.xPos} onChange={this.handleChange} placeholder="enter x" type="number" min="0"></input>
                            </label>
                            <label htmlFor="yPos">
                                <span> Y </span>
                                <input id="yPos" value={this.state.formData.yPos} onChange={this.handleChange} placeholder="enter y" type="number" min="0"></input>
                            </label>
                        </div>
                        <div className="input-group">
                            <label htmlFor="width">
                                <span> Width </span>
                                <input id="width" value={this.state.formData.width} onChange={this.handleChange} placeholder="enter width" type="number" min="0"></input>
                            </label>
                            <label htmlFor="height">
                                <span> Height </span>
                                <input id="height" value={this.state.formData.height} onChange={this.handleChange} placeholder="enter height" type="number" min="0"></input>
                            </label>
                        </div>
                        <input class="add-box-btn" type="submit" value="Add Box" />
                    </form>
                    <div>
                    </div>
                </div>
            </div>
        )
    }
}


export default ImageEditor
