import React, { useState, useRef, useLayoutEffect } from 'react'
import BBox from './BBox'

const ImageEditor = ({ source, index }) => {

    const [bBoxes, setBBoxes] = useState([])
    const [formData, setFormData] = useState({ xPos: 0, yPos: 0, width: 0, height: 0 })
    const [hintMessage, setHintMessage] = useState('')

    const imageRef = useRef(null)


    // Adds a Box to the Array and Resets the Form
    const handleSumbit = (e) => {
        e.preventDefault()
        let xPos = 0
        let yPos = 0
        let width = 0
        let height = 0

        // Check if Integers
        try {
            xPos = parseInt(formData.xPos) 
            yPos = parseInt(formData.yPos)
            width = parseInt(formData.width)
            height = parseInt(formData.height)
        }
        catch {
            setHintMessage('Please enter valid numbers.')
        }

        const newBBox = {
            xPos: (xPos / imageRef.current.clientWidth) * 100,
            yPos: (yPos / imageRef.current.clientHeight) * 100,
            width: (width / imageRef.current.clientWidth) * 100,
            height: (height / imageRef.current.clientHeight) * 100
        }

        console.log()

        // Check if within image bounds 
        if (newBBox.xPos + newBBox.width <= 100
            && newBBox.yPos + newBBox.height <= 100) {
            bBoxes.push(newBBox)
            setBBoxes(bBoxes)
            setFormData({ xPos: 0, yPos: 0, width: 0, height: 0 })
        }
        else {
            setHintMessage('Your box overflows the image. Please draw a box that fits into ' + imageRef.current.clientWidth + 'px x ' + imageRef.current.clientHeight + 'px.')
        }

    }

    // Dynamically syncs FormData with input values
    const handleChange = (e) => {
        const updatedValue = { [e.target.id]: e.target.value }
        setFormData(formData => ({
            ...formData, ...updatedValue
        }))
    }

    return (
        <div>

            { /* ----------- IMAGE AND BOXES ----------- */}

            <div className="image-container">
                <img ref={imageRef} src={source} alt={'the uploaded image: ' + (index + 1)} />
                {
                    bBoxes.map((value, i) => {
                        return (
                            <BBox key={i} props={value} />
                        )
                    })
                }

            </div>
            <div className="editor">



                { /* ----------- FORM AREA ----------- */}

                <p className="hint-message">{hintMessage}</p>

                <form onSubmit={handleSumbit}>
                    <div className="input-group">
                        <label htmlFor="xPos">
                            <span> X </span>
                            <input id="xPos" value={formData.xPos} onChange={handleChange} placeholder="0" type="number" min="0"></input>
                        </label>
                        <label htmlFor="yPos">
                            <span> Y </span>
                            <input id="yPos" value={formData.yPos} onChange={handleChange} placeholder="0" type="number" min="0"></input>
                        </label>
                    </div>
                    <div className="input-group">
                        <label htmlFor="width">
                            <span> Width </span>
                            <input id="width" value={formData.width} onChange={handleChange} placeholder="0" type="number" min="0"></input>
                        </label>
                        <label htmlFor="height">
                            <span> Height </span>
                            <input id="height" value={formData.height} onChange={handleChange} placeholder="0" type="number" min="0"></input>
                        </label>
                    </div>
                    <input type="submit" />
                </form>
                <div>
                </div>
            </div>
        </div>
    )
}

export default ImageEditor
