import React from 'react'
import BBox from './BBox'

class ImageEditor extends React.Component {

    constructor(props) {
        super(props)

        this.state = { formData: { xPos: 0, yPos: 0, width: 0, height: 0 }, bBoxes: [], message: '', sizeHint: ''}


        this.imageRef = null


        this.setImageRef = element => {
            this.imageRef = element
        }

        // Dynamically syncs FormData with input values
        this.handleChange = (e) => {
            this.setState({...this.state, formData: { ...this.state.formData, [e.target.id]: e.target.value}})
            console.log(this.state)
        }


        // Adds a Box to the Array and Resets the Form
        this.handleSumbit = (e) => {
            e.preventDefault()

            // Check if > 0 
            if (this.formData.width <= 0 || this.formData.height <= 0) {
                this.message = 'Please add a width and height'
                console.log(this.message)
                return
            }

            const newBBox = {
                    xPos: (this.formData.xPos / this.imageRef.clientWidth) * 100,
                    yPos: (this.formData.yPos / this.imageRef.clientHeight) * 100,
                    width: (this.formData.width / this.imageRef.clientWidth) * 100,
                    height: (this.formData.height / this.imageRef.clientHeight) * 100
                }


            // Check if within image bounds 
            if (newBBox.xPos + newBBox.width > 100
                || newBBox.yPos + newBBox.height > 100) {
                this.message = 'Your box overflows the image. Please draw a box that fits into ' + this.imageRef.current.clientWidth + 'px x ' + this.imageRef.current.clientHeight + 'px.'
                console.log(this.message)
                return 
            }

            // Create new Box
                this.state.bBoxes.push(newBBox)
                this.formData = { xPos: 0, yPos: 0, width: 0, height: 0 }
                this.message = 'Box added'
                console.log(this.bBoxes)

        }


    }

    render() {
        return (
            <div id={this.props.index}>

                { /* ----------- IMAGE AND BOXES ----------- */}

                <div className="image-container">
                    <img ref={this.setImageRef} src={this.props.source} alt={'the uploaded image: ' + (this.props.index + 1)} />
                    {
                        this.state.bBoxes.map((value, index) => {
                            return (
                                <BBox key={index} props={value} />
                            )
                        })
                    }
                </div>
                <div className="editor">



                    { /* ----------- FORM AREA ----------- */}

                    <p className="hint-message">{this.message}</p>

                    <form onSubmit={this.handleSumbit}>
                        <div className="input-group">
                            <label htmlFor="xPos">
                                <span> X </span>
                                <input id="xPos" value={this.state.formData.xPos} onChange={this.handleChange} placeholder="0" type="number" min="0"></input>
                            </label>
                            <label htmlFor="yPos">
                                <span> Y </span>
                                <input id="yPos" value={this.state.formData.yPos}  onChange={this.handleChange} placeholder="0" type="number" min="0"></input>
                            </label>
                        </div>
                        <div className="input-group">
                            <label htmlFor="width">
                                <span> Width </span>
                                <input id="width" value={this.state.formData.width} onChange={this.handleChange} placeholder="0" type="number" min="0"></input>
                            </label>
                            <label htmlFor="height">
                                <span> Height </span>
                                <input id="height" value={this.state.formData.height} onChange={this.handleChange} placeholder="0" type="number" min="0"></input>
                            </label>
                        </div>
                        <input type="submit" />
                    </form>
                    <div>
                    </div>
                </div>
            </div>
        )
    }
}


export default ImageEditor
