import React, {useState} from 'react'
import BBox from './BBox'

const ImageEditor = ()  => {

    const [bBoxes, setBBoxes] = useState([])
    const [formData, setFormData] = useState({})

    const handleSumbit = (e) => {
        console.log('handleSumbit')
        const newBBox = {...formData}
        console.log(newBBox)
        bBoxes.push(newBBox)
        setBBoxes(bBoxes)
        setFormData({})
        e.preventDefault()
        console.log(bBoxes)
    }

    const handleChange = (e) => {
        const updatedValue = {[e.target.id]: e.target.value}
        setFormData(formData => ({
            ...formData, ...updatedValue
        }))
    }

    return (
       <div>
            <div className="imageContainer">
                {
                    bBoxes.map((item, index) => {
                        return (
                            <BBox key={index} style={item}/>
                        )
                    })
                }

            </div>
            <div className="editor">
                <form onSubmit={handleSumbit}>
                    <div className="input-group">
                        <label htmlFor="xPos">
                            <span> X </span>
                            <input id="xPos" onChange={handleChange} placeholder="0" type="number" min="0" max={100}></input>
                        </label>
                        <label htmlFor="yPos">
                            <span> Y </span>
                            <input id="yPos" onChange={handleChange} placeholder="0"  type="number" min="0" max={100}></input>
                        </label>
                    </div>
                    <div className="input-group">
                        <label htmlFor="width">
                            <span> Width </span>
                            <input id="width" onChange={handleChange} placeholder="0"  type="number" min="0" max={100}></input>
                        </label>
                        <label htmlFor="height">
                            <span> Height </span>
                            <input id="height" onChange={handleChange} placeholder="0" type="number" min="0" max={1000}></input>
                        </label>
                    </div>
                    <input type="submit" />
                    <button>Delete Image</button>
                </form>
                <div>
                </div>
            </div>
        </div>
    )
}

export default ImageEditor
