import React, { useState } from 'react'
import './style/index.css';
import UploadIcon from './img/upload.svg'
import ImageEditor from './components/ImageEditor';

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = { images: [] }

    this.handleUpload = this.handleUpload.bind(this)
    this.deleteImage = this.deleteImage.bind(this)

  }


  handleUpload = (e) => {

      var imageEl = new Image()
      var scaleFactor = 0
      var image = {}
      imageEl.onload = function () {
        const targetWidth = window.innerWidth * 0.9
        scaleFactor = targetWidth/imageEl.width
        image = { src: URL.createObjectURL(e.target.files[0]), scaleFactor: scaleFactor}
        this.setState({ images: [...this.state.images, image] })
      }

    e.target.value = ""
  }


  deleteImage = (image) => {
    var filteredImages = this.state.images.filter(i => {
      return i !== image
    })
    this.setState({ images: filteredImages })
    URL.revokeObjectURL(image)
  }


  render() {
    return (
      <div className="App">


        {/*---- LIST OF IMAGE EDITORS ---- */}
        {this.state.images.map((image, index) => {
          return (
            <div key={index}>
              <ImageEditor image={image} index={index} />
              <button onClick={() => this.deleteImage(image)}> delete </button>
            </div>
          )
        })}

        {/*---- ADD BUTTON ---- */}
        <form>
          <label id="upload-image-button" htmlFor="file"> Add new Image
            <input onChange={this.handleUpload} type="file" accept="image/jpeg, image/png" name="image" id="file" /></label>
        </form>
      </div>
    );
  }
}

export default App;
